import React, { Component } from "react";
import "./Content.css";
import { Tab, Tabs, Table } from 'react-bootstrap';
import axios from 'axios';
import UserList from "../components/user/list"

const PENDING = "Pending";
const APPROVED = "Approved";
const DENY = "Deny";

class UserManagement extends Component{
  state = {
    pending:[],
    approved:[],
    deny:[]
  };


  // componentDidMount() {
  //   axios.get('http://127.0.0.1:5000/users')
  //   .then(response => response.data)
  //    .then(data => {
  //     //this.setState({ users:data})
  //     this.setState({ users: JSON.parse(response.data.body) });
  //     console.log(this.state.users)
  //     })
  //    .catch((err) =>{
  //     console.log("There is an error in API call.", err)
  //   })
  // }

  componentDidMount() {
    axios.get('http://127.0.0.1:5000/users')

      .then(res => {
        //JSON.parse(res.data)
        // console.log("data: --- ", res.data.map(item =>  console.log(item)));
        console.log(res.data)
        let users = JSON.parse(res.data.data);
        let pending = [];
        let approved = [];
        let deny = [];
        for(let user of users){
          switch(user.approval){
            case PENDING:
              pending.push(user);
              break;
            case APPROVED:
              approved.push(user)
              break;
            case DENY:
              deny.push(user);
              break;
            default:
              console.log("approval type not defined....", user.approval)
          }
        }
        this.setState({ 
          pending:pending,
          approved:approved,
          deny:deny
        });
      }) 

      .catch((err) =>{
        console.log("There is an error in API call.", err)
      })
  }


  render(){
    return(
      <>
      <div className="workSection">
        <div className="heading">
          <h1>User Management</h1>
        </div>
        <div className="workAreaBg">
          <div className="formcontainer">
            <div className="showAttendance tabing">
            <Tabs defaultActiveKey="Pending" id="uncontrolled-tab-example">
                <Tab eventKey="Pending" title="Pending">
                  <div className="managmentArea">
                  <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th className="checkBox">
                          <input type="checkbox" name="check"/>
                        </th>
                        <th>User Id</th>
                        <th>Facebook Id</th>
                      </tr>
                    </thead>
                    <tbody>
                    {this.state.pending.map(({userid,fid }) => (
                          <tr>
                          <td className="checkBox">
                          <input type="checkbox" name="check"/>
                          </td>
                          <td>{userid}</td> 
                          <td>{fid}</td>                  
                       </tr>

                      ))}

                    </tbody>
                    <tfoot>
                        <tr>
                          <td colSpan="3">
                          <button type="button" className="btn btn-primary">
                            Approved
                          </button>

                          </td>

                        </tr>
                      </tfoot>
                  </Table>
                </div>
                </Tab>
                <Tab eventKey="proApprovedfile" title="Approved">
                  <UserList users={this.state.approved} />
                </Tab>
                <Tab eventKey="Deny" title="Deny">
                  <UserList users={this.state.deny} />
                </Tab>
            </Tabs>
            </div>
          </div>
        </div>
      </div>
    </>
    )
  }
}

export default UserManagement;
